import { Accounts } from 'meteor/accounts-base';

Accounts.onCreateUser((options, user) => {

  user.stats = {
    lifetimePoints : 0,
    lifetimeWins : 0,
    lifetimeGamesPlayed : 0
  };

  if (options.profile)
    user.profile = options.profile;
  return user;
});

Meteor.publish(null, function() {
    if (this.userId) {
         return Meteor.users.findOne(this.userId, {fields: user.stats = {
                                                            lifetimePoints : 0,
                                                            lifetimeWins : 0,
                                                            lifetimeGamesPlayed : 0
                                                          }
    });
    }else{
        this.ready();
    }
})
