import React, { Component, PropTypes } from 'react';

// Room component - represents a single room item
export default class Room extends Component {
  gotoRoom(e) {
    var theroom = $(e.target).attr("data-room")
    $(".mainScreen, .popup").hide();
    $(".chat").removeAttr("id").attr("id", theroom);
    $(".chat").show();
  }

  render() {
    return (<li><button className="roomSelect" data-room={this.props.room.room_id} onClick={this.gotoRoom.bind(this)}>Join</button>
        <span className="roomlistTitle">{this.props.room.displayName}</span>
        <span className="roomlistRound"> Rnd: {this.props.room.round}</span>
        <span className="roomlistPlayers"> Players: {this.props.room.players}</span></li>
    );
  }
}

Room.propTypes = {
  // This component gets the room to display through a React prop.
  // We can use propTypes to indicate it is required
  room: PropTypes.object.isRequired,
};
