import { Accounts } from 'meteor/accounts-base';

Accounts.onCreateUser((options, user) => {

  user.stats = {
    lifetimePoints : 0,
    lifetimeWins : 0,
    lifetimeGamesPlayed : 0
  };

  if (options.profile)
    user.profile = options.profile;

  return user;
});

var userId = this.userId

Meteor.methods({
    lifePoints: function (userId, lpoints) {
      Meteor.users.update(userId, stats.lifetimePoints);
    },
    lifeWins: function (userId, lwins) {
      Meteor.users.update(userId, stats.lifetimeWins);
    },
    lifeGames: function (userId, lgames) {
      Meteor.users.update(userId, stats.lifetimeGamesPlayed);
    }
  });

Meteor.call('lifePoints', userId, 0);
