  showLifePoints() {
      return meteorDataContainer.currentUsername;
  }


  render() {
    return (
      <div className="container">
        <header>
          <h1>Welcome to AcroParty! Life wins is {this.showLifePoints()}</h1>
        </header>
        <AccountsUIWrapper />

      </div>
    );
  }
}
