import { Accounts } from 'meteor/accounts-base';
console.log("accounts-server-config ran!")

Accounts.onCreateUser((options, user) => {
  console.log("oncreateuser ran!")

  user.stats = {
    lifetimePoints : 0,
    lifetimeWins : 0,
    lifetimeGamesPlayed : 0
  }

  if (options.profile)
    user.profile = options.profile;

  return user;
});
