import React, { Component, PropTypes } from 'react';
import { createContainer } from 'meteor/react-meteor-data';
import { gameRoomIdSelected } from './App.jsx'

import { Rooms } from '../api/rooms.js';
import Room from './Room.jsx';

export default class Game extends Component {
  render() {
    return ( <div>{this.props.selectedRoom.players.length > 1 ? "<div>This is the game area!</div>" :
      "<div>THERE IS MORE THAN ONE PLAYER HERE!</div>"}</div>
    );
  }
}

export default createContainer(() => {
  //var userRoom = Meteor.user().currentRoom;
  return {
    currentUser: Meteor.user(),
    selectedRoom: Rooms.findOne({ room_id: gameRoomIdSelected.get() }),
  };
}, Game);

